import React, { createContext, useState, useMemo } from 'react';

// Creación del contexto con valores iniciales
export const GlobalContext = createContext({
  theme: 'light', // Valor inicial para el tema
  user: null, // Valor inicial para la información del usuario (null si no está autenticado)
  toggleTheme: () => {}, // Función para cambiar el tema
  login: () => {}, // Función para manejar el inicio de sesión
  logout: () => {} // Función para manejar el cierre de sesión
});

// Componente Proveedor
export const GlobalProvider = ({ children }) => {
  const [theme, setTheme] = useState('light');
  const [user, setUser] = useState(null);

  // Función para alternar el tema
  const toggleTheme = () => {
    setTheme(currentTheme => (currentTheme === 'light' ? 'dark' : 'light'));
  };

  // Función para manejar el inicio de sesión
  const login = (userData) => {
    setUser(userData);
  };

  // Función para manejar el cierre de sesión
  const logout = () => {
    setUser(null);
  };

  // Memorizar el valor del contexto para optimizar el rendimiento
  const contextValue = useMemo(() => ({
    theme,
    user,
    toggleTheme,
    login,
    logout
  }), [theme, user]);

  return (
    <GlobalContext.Provider value={contextValue}>
      {children}
    </GlobalContext.Provider>
  );
};
