import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { ThemeContext } from './ThemeContext'; // Asume que tienes un ThemeContext definido en otro lugar

const Navbar = () => {
  // Usamos el contexto para acceder al tema actual y la función para cambiarlo
  const { theme, toggleTheme } = useContext(ThemeContext);

  return (
    <nav style={{ backgroundColor: theme === 'dark' ? '#333' : '#fff', color: theme === 'dark' ? '#fff' : '#333' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '0 20px' }}>
        <span style={{ fontSize: '1.5em', fontWeight: 'bold' }}>DIENTITO</span>
        <div>
          <Link to="/dentistas" style={{ margin: '0 10px', textDecoration: 'none', color: theme === 'dark' ? '#fff' : '#333' }}>Dentistas</Link>
          <Link to="/favoritos" style={{ margin: '0 10px', textDecoration: 'none', color: theme === 'dark' ? '#fff' : '#333' }}>Favoritos</Link>
          <Link to="/contactanos" style={{ margin: '0 10px', textDecoration: 'none', color: theme === 'dark' ? '#fff' : '#333' }}>Contáctanos</Link>
        </div>
        <button onClick={toggleTheme} style={{ padding: '10px 20px', fontSize: '1em' }}>
          Cambiar tema
        </button>
      </div>
    </nav>
  );
};

export default Navbar;
