import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { GlobalContext } from '../Context/GlobalProvider'; // Asegúrate de que la ruta es correcta
import styles from '../Styles/Navbar.module.scss';

class Navbar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isMenuOpen: false
    };
  }

  toggleMenu = () => {
    this.setState(prevState => ({
      isMenuOpen: !prevState.isMenuOpen
    }));
  }

  static contextType = GlobalContext; // Asigna el contexto a la clase

  render() {
    const { theme, toggleTheme } = this.context; // Accede al contexto
    const { isMenuOpen } = this.state;
    const themeClass = theme === 'dark' ? 'dark' : 'light';

    return (
      <nav className={`${styles.navbar} ${styles[themeClass]}`}>
        <div className={styles.navContent}>
          <input id="checkbox" type="checkbox" className={styles.checkbox} />
          <label className={styles.toggle} htmlFor="checkbox">
            <div className={styles.bars} id="bar1"></div>
            <div className={styles.bars} id="bar2"></div>
            <div className={styles.bars} id="bar3"></div>
          </label>
          <div className={styles.navItems}>
            <Link to="/dentistas" className={`${styles.navItem} ${styles[themeClass]}`}>Dentistas</Link>
            <Link to="/favoritos" className={`${styles.navItem} ${styles[themeClass]}`}>Favoritos</Link>
            <Link to="/contactanos" className={`${styles.navItem} ${styles[themeClass]}`}>Contáctanos</Link>
            <button onClick={toggleTheme} className={styles.navButton}>
              Cambiar tema
            </button>
          </div>
        </div>
      </nav>
    );
  }
}

export default Navbar;
