import React, { useState, useContext } from 'react';
import { GlobalContext } from '../../Context/GlobalProvider'; // Asegúrate de que la ruta sea correcta
import styles from '../../Styles/Form.module.scss'; // Asegúrate de tener estilos específicos para el Form si es necesario

const Form = () => {
  const { theme } = useContext(GlobalContext);
  const [formData, setFormData] = useState({
    name: '',
    email: ''
  });
  const [message, setMessage] = useState('');
  const [submitted, setSubmitted] = useState(false);

  const validateForm = () => {
    // Validación de nombre
    if (formData.name.length <= 5) {
      setMessage('Por favor verifique su información nuevamente');
      return false;
    }
    // Validación de email
    if (!/\S+@\S+\.\S+/.test(formData.email)) {
      setMessage('Por favor verifique su información nuevamente');
      return false;
    }
    return true;
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (validateForm()) {
      setMessage(`Gracias ${formData.name}, te contactaremos cuando antes vía mail`);
      setSubmitted(true);
    } else {
      setSubmitted(false);
    }
  };

  const handleChange = (event) => {
    setFormData({
      ...formData,
      [event.target.name]: event.target.value
    });
    setMessage(''); // Resetear mensaje de error al comenzar a editar nuevamente
  };

  return (
    <div className={`${styles.formContainer} ${styles[theme]}`}>
      <form onSubmit={handleSubmit}>
        <label htmlFor="name">Nombre completo:</label>
        <input
          type="text"
          id="name"
          name="name"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <button type="submit">Enviar</button>
        {message && <p>{message}</p>}
      </form>
    </div>
  );
};

export default Form;
